                                                                                                                                                                                                                                                                                        pg_get_functiondef                                                                                                                                                                                                                                                                                        
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
 CREATE OR REPLACE FUNCTION public.get_simple_admin_payments_data(days_back integer)                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             +
  RETURNS TABLE(artist_id uuid, artist_name text, artist_email text, artist_phone text, artist_entry_id integer, artist_country text, artist_person_id uuid, artist_created_at timestamp with time zone, estimated_balance numeric, payment_account_status text, stripe_recipient_id text, recent_city text, recent_contests bigint, is_recent_contestant boolean, payment_pending_count bigint, payment_processing_count bigint, payment_completed_count bigint, payment_failed_count bigint, payment_manual_count bigint, latest_payment_status text, currency_code text, currency_symbol text)+
  LANGUAGE plpgsql                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               +
 AS $function$                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   +
 BEGIN                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           +
   RETURN QUERY                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  +
   WITH recent_contestants AS (                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  +
     SELECT                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      +
       ap.id as artist_id,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       +
       COUNT(DISTINCT r.id) as contest_count,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    +
       COALESCE(c.name, 'Unknown') as city_name                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  +
     FROM round_contestants rc                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   +
     JOIN rounds r ON rc.round_id = r.id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         +
     JOIN events e ON r.event_id = e.id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          +
     JOIN artist_profiles ap ON rc.artist_id = ap.id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             +
     LEFT JOIN cities c ON e.city_id = c.id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      +
     WHERE e.event_start_datetime >= NOW() - (days_back || ' days')::INTERVAL                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    +
     GROUP BY ap.id, c.name                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      +
   ),                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            +
   art_sales AS (                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                +
     SELECT                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      +
       ap.id as artist_id,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       +
       -- CRITICAL FIX: Only count 'paid' status                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 +
       SUM(COALESCE(a.final_price, a.current_bid, 0) * e.artist_auction_portion) as sales_total                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  +
     FROM art a                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  +
     JOIN artist_profiles ap ON a.artist_id = ap.id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              +
     JOIN events e ON a.event_id = e.id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          +
     WHERE a.status = 'paid'  -- CRITICAL FIX: Only paid status                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  +
       AND COALESCE(a.final_price, a.current_bid, 0) > 0                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         +
     GROUP BY ap.id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              +
   ),                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            +
   payment_debits AS (                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           +
     SELECT                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      +
       ap.artist_profile_id,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     +
       SUM(ap.gross_amount) as debits_total                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      +
     FROM artist_payments ap                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     +
     WHERE ap.status IN ('completed', 'paid')                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    +
     GROUP BY ap.artist_profile_id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               +
   ),                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            +
   payment_history AS (                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          +
     SELECT                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      +
       ap.artist_profile_id,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     +
       COUNT(*) FILTER (WHERE ap.status = 'pending') as pending_count,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           +
       COUNT(*) FILTER (WHERE ap.status = 'processing') as processing_count,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     +
       COUNT(*) FILTER (WHERE ap.status = 'completed') as completed_count,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       +
       COUNT(*) FILTER (WHERE ap.status = 'failed') as failed_count,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             +
       COUNT(*) FILTER (WHERE ap.payment_type = 'manual') as manual_count                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        +
     FROM artist_payments ap                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     +
     GROUP BY ap.artist_profile_id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               +
   ),                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            +
   latest_payments AS (                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          +
     SELECT DISTINCT ON (ap.artist_profile_id)                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   +
       ap.artist_profile_id,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     +
       ap.status as latest_status                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                +
     FROM artist_payments ap                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     +
     ORDER BY ap.artist_profile_id, ap.created_at DESC                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           +
   )                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             +
   SELECT                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        +
     ap.id::UUID,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                +
     ap.name::TEXT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              +
     ap.email::TEXT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             +
     ap.phone::TEXT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             +
     ap.entry_id::INTEGER,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       +
     ap.country::TEXT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           +
     ap.person_id::UUID,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         +
     ap.created_at::TIMESTAMPTZ,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 +
     GREATEST(0, COALESCE(asales.sales_total, 0) - COALESCE(pd.debits_total, 0))::NUMERIC,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       +
     CASE                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        +
       WHEN agp.status = 'ready' THEN 'ready'                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    +
       WHEN agp.status = 'invited' THEN 'invited'                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                +
       ELSE 'no_account'                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         +
     END::TEXT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  +
     agp.stripe_recipient_id::TEXT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              +
     COALESCE(rc.city_name, 'Unknown')::TEXT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    +
     COALESCE(rc.contest_count, 0)::BIGINT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      +
     (rc.contest_count IS NOT NULL AND rc.contest_count > 0)::BOOLEAN,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           +
     COALESCE(ph.pending_count, 0)::BIGINT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      +
     COALESCE(ph.processing_count, 0)::BIGINT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   +
     COALESCE(ph.completed_count, 0)::BIGINT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    +
     COALESCE(ph.failed_count, 0)::BIGINT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       +
     COALESCE(ph.manual_count, 0)::BIGINT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       +
     lp.latest_status::TEXT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     +
     COALESCE(co.currency_code, 'USD')::TEXT,                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    +
     COALESCE(co.currency_symbol, '$')::TEXT                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     +
   FROM artist_profiles ap                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       +
   LEFT JOIN artist_global_payments agp ON ap.id = agp.artist_profile_id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         +
   LEFT JOIN countries co ON ap.country = co.code                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                +
   LEFT JOIN recent_contestants rc ON ap.id = rc.artist_id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       +
   LEFT JOIN art_sales asales ON ap.id = asales.artist_id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        +
   LEFT JOIN payment_debits pd ON ap.id = pd.artist_profile_id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   +
   LEFT JOIN payment_history ph ON ap.id = ph.artist_profile_id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  +
   LEFT JOIN latest_payments lp ON ap.id = lp.artist_profile_id                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  +
   WHERE GREATEST(0, COALESCE(asales.sales_total, 0) - COALESCE(pd.debits_total, 0)) > 0.01                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      +
   ORDER BY ap.name;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             +
 END;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            +
 $function$                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      +
 
(1 row)

